import React, { useEffect, useState } from "react";
import axios from 'axios';
import Blog from "./Blog";

const UserBlogs = () => {
    const [blogs, setBlogs] = useState();
    const id = localStorage.getItem("userID");
    const sendRequest = async() => {
        const res = await axios.get(`http://localhost:5001/user/${id}`).catch((err)=>console.log(err));
        const data = await res.data;
        return data;
    };
    useEffect(() => {
        sendRequest().then((data)=>setBlogs(data.blogs.blogs));
    },[]);
    console.log(blogs);
    return (
        <div>
            { blogs && blogs.map((blog,index) => (
          <Blog title={blog.title} description={blog.description} imageURL={blog.image} username={blog.user.name} />
         ))}
        </div>
    );
}

export default UserBlogs;
