import React from "react";
import { AppBar, Toolbar, Typography, Box, Button, Tabs } from '@mui/material';
import { NavLink } from "react-router-dom";

const Header = () => {
    return (
        <AppBar sx={{background:"radial-gradient(circle, rgba(238,174,202,1) 0%, rgba(148,148,233,1) 100%);"}}>
            <Toolbar>
                <Typography variant="h4">Blog App</Typography>
                <Box>
                    <Tabs>
                        <Tab LinkComponent={NavLink} to="/blogs/add" label="Add Blog" />
                        <Tab LinkComponent={NavLink} to="/blogs" label="All Blogs" />
                    </Tabs>
                </Box>
                <Box display="flex" marginLeft="auto">
                    <Button LinkComponent={NavLink} to="/login" variant='contained' sx={{margin:2, borderRadius: 10}}>Login</Button>
                    <Button LinkComponent={NavLink} to="/signup" variant='contained' sx={{margin:2, borderRadius: 10}}>Sign-up</Button>
                </Box>
            </Toolbar>
        </AppBar>
    )
}

export default Header
