import { createSlice } from 'react-redux'

const initialState = {
    loggedIn: false,
};

const slice = createSlice({
    name: 'login',
    initialState,
    reducers: {
    login(state){
        state.loggedIn = true
    },
    logout(state){
        state.loggedIn = false
    },
},
})

export const actions = slice.actions;
export const store = configureStore({
    reducer: slice.reducer
});
